    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       
        //Programador
        ProgramadorDto objProgamadorDto = new ProgramadorDto();
        objProgamadorDto.setSalario(Integer.parseInt(txtProgramador.getText()));
        objProgamadorDto.setHorasAdd(Integer.parseInt(txtProgramadorAdd.getText()));
        
        ProgramadorDao SalarioTotalPg = new ProgramadorDao(); 
        SalarioTotalPg.calcularSalario(objProgamadorDto);        
        
        //Gerente
        GerenteDto objGerenteDto = new GerenteDto();
        objGerenteDto.setSalarioGerente(Integer.parseInt(txtGerente.getText()));
        objGerenteDto.setHorasAdd(Integer.parseInt(txtGerenteAdd.getText()));
        
        GerenteDao SalarioTotalGr = new GerenteDao();
        SalarioTotalGr.calcularSalarioGerente(objGerenteDto);
        
    }//GEN-LAST:event_jButton1ActionPerformed

